    public int candy(int[] ratings) {

        if (ratings == null || ratings.length <= 0) {
            return 0;
        }
        int n = ratings.length;
        int[] vals = new int[n];
        vals[0] = 1;
        boolean des;
        int desStart = 0;
        for (int i = 1; i < n; i++) {
            if (ratings[i] == ratings[i - 1]) {
                desStart = i;
                vals[i] = 1;
                des = false;
            } else if (ratings[i] < ratings[i - 1]) {
                des = true;
            } else {
                vals[i] = vals[i - 1] + 1;
                desStart = i;
                des = false;
            }
            if (des && (i == n - 1 || ratings[i + 1] >= ratings[i])) {
                vals[i] = 1;
                for (int j = i - 1; j > desStart; j--) {
                    vals[j] = vals[j + 1] + 1;
                }
                if (vals[desStart] <= vals[desStart + 1]) {
                    vals[desStart] = vals[desStart + 1] + 1;
                }
            }

        }
        int sum = 0;
        for (int i = 0; i < n; i++) {
            //System.out.print(vals[i] + ", ");
            sum += vals[i];
        }
        return sum;
    }

